mapper.select:
SELECT project_title , t.team_seq , team_name , DATE_FORMAT(t.reg_date, '%Y-%m-%d %H:%i:%s') reg_date FROM tbl_team t
JOIN tbl_project p ON t.project_seq = p.project_seq JOIN tbl_project_member pm ON p.project_seq = pm.project_seq JOIN tbl_user u ON pm.user_seq = u.user_seq WHERE u.user_seq = #{userSeq} AND pm.project_mentor_yn = 'Y' AND pm.project_member_del_yn = 'N' AND team_status = 'OPEN'
JOIN tbl_project p ON t.project_seq = p.project_seq JOIN tbl_team_member tm ON tm.team_seq = t.team_seq WHERE tm.user_seq = #{userSeq}
